import React, { Component } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import Tabs from "react-bootstrap/Tabs";
import Tab from "react-bootstrap/Tab";
// import Sonnet from "react-bootstrap/Sonnet";

import Accordion from "react-bootstrap/Accordion";
// import DataComponent from "./Sheets";
import CustomComponent from "./Sheets";
import axios from "axios";
import { Chart } from "react-charts";
import {
  LineChart,
  Line,
  CartesianGrid,
  Legend,
  XAxis,
  YAxis,
  Tooltip,
} from "recharts";

const RenderLineChart = (data, dataSheet) => {
  var newArr2 = data.dataSheet.flat(2);
  var Arr = [];
  for (var i = 0; i < newArr2.length; i++) {
    Arr[i] = newArr2[i].replace(/[!@#$%^&*]/g, "");
  }
  var num = [-50, 0, 50];
  var newArr = data.data.map(function (x, index) {
    return { name: x, percent: Arr[index], height: num[index] };
  });
  return (
    <LineChart
      width={900}
      height={500}
      data={newArr}
      margin={{ top: 5, right: 20, bottom: 5, left: 0 }}
      style={{ margin: "3% auto" }}
    >
      <Line type="monotone" dataKey="percent" stroke="#007bff" />
      {/* <CartesianGrid stroke="#ccc" strokeDasharray="5 5" /> */}
      <XAxis dataKey="name" padding={{ left: 30, right: 30 }} />
      <YAxis dataKey="height" />
      <Tooltip />
      <Legend />
    </LineChart>
  );
};

